t.script:
                // Wait for DOM to be fully loaded
                document.addEventListener('DOMContentLoaded', function() {
                // Get elements
                const newEntryBtn = document.getElementById('newEntryBtn');
                const firstEntryBtn = document.getElementById('firstEntryBtn');
                const closeModalBtn = document.getElementById('closeModalBtn');
                const cancelModalBtn = document.getElementById('cancelModalBtn');
                const modal = document.getElementById('newEntryModal');
                const backdrop = document.getElementById('modalBackdrop');
                const entryTypeSelect = document.getElementById('entryTypeSelect');
                const onceFields = document.getElementById('onceFields');
                const frequentFields = document.getElementById('frequentFields');
                const entryForm = document.getElementById('entryForm');

                // Function to open modal
                function openModal() {
                modal.style.display = 'block';
                backdrop.style.display = 'block';
                document.body.style.overflow = 'hidden';
                }

                // Function to close modal
                function closeModal() {
                modal.style.display = 'none';
                backdrop.style.display = 'none';
                document.body.style.overflow = 'auto';
                entryForm.reset();
                toggleEntryFields();
                }

                // Function to toggle fields based on entry type
                function toggleEntryFields() {
                const entryType = entryTypeSelect.value;

                if (entryType === 'once') {
                onceFields.style.display = 'block';
                frequentFields.style.display = 'none';
                } else if (entryType === 'frequent') {
                onceFields.style.display = 'none';
                frequentFields.style.display = 'block';
                } else {
                onceFields.style.display = 'none';
                frequentFields.style.display = 'none';
                }
                }

                // Event Listeners
                if (newEntryBtn) {
                newEntryBtn.addEventListener('click', openModal);
                }

                if (firstEntryBtn) {
                firstEntryBtn.addEventListener('click', openModal);
                }

                if (closeModalBtn) {
                closeModalBtn.addEventListener('click', closeModal);
                }

                if (cancelModalBtn) {
                cancelModalBtn.addEventListener('click', closeModal);
                }

                // Close modal when clicking on backdrop
                backdrop.addEventListener('click', closeModal);

                // Close modal with Escape key
                document.addEventListener('keydown', function(event) {
                if (event.key === 'Escape' & modal.style.display === 'block') {
                closeModal();
                }
                });

                // Toggle fields when entry type changes
                if (entryTypeSelect) {
                entryTypeSelect.addEventListener('change', toggleEntryFields);
                }

                // Initialize fields
                toggleEntryFields();

                // Prevent form submission if validation fails
                entryForm.addEventListener('submit', function(event) {
                const categoryId = entryForm.querySelector('[name="category_id"]').value;
                const entryType = entryForm.querySelector('[name="entry_type"]').value;

                if (!categoryId || !entryType) {
                event.preventDefault();
                alert('Please fill in all required fields.');
                return false;
                }

                // Additional validation based on entry type
                if (entryType === 'once') {
                const visitDate = entryForm.querySelector('[name="visit_date"]').value;
                const startTime = entryForm.querySelector('[name="start_time"]').value;
                const validFor = entryForm.querySelector('[name="valid_for"]').value;

                if (!visitDate || !startTime || !validFor) {
                event.preventDefault();
                alert('Please fill in all required fields for one-time visit.');
                return false;
                }
                } else {
                const timeFrom = entryForm.querySelector('[name="time_from"]').value;
                const timeTo = entryForm.querySelector('[name="time_to"]').value;
                const validity = entryForm.querySelector('[name="validity"]').value;
                const entriesPerDay = entryForm.querySelector('[name="entries_per_day"]').value;

                if (!timeFrom || !timeTo || !validity || !entriesPerDay) {
                event.preventDefault();
                alert('Please fill in all required fields for frequent visit.');
                return false;
                }
                }

                // Form is valid, allow submission
                return true;
                });
                });

document.addEventListener('DOMContentLoaded', function() {
// Add any JavaScript needed for the detail view here
console.log('Visiting Help Entry Detail loaded');
});
